import React from "react";
import "../index.css";
import NavigationBar from "./Navbar";
import firebase from "./Firestore";
import FooterPage from "./Footer";
import Card from "react-bootstrap/Card";
import Button from "react-bootstrap/Button";
import Media from "react-bootstrap/Media";
import Table from "react-bootstrap/Table";
import { Container} from "react-bootstrap";
import "firebase/storage";

class hamburgerMenuPage extends React.Component {
  
  //Tonen van alle build die de gebruiker al heeft gemaakt

  //Aanmake van state variabelen
  /*
  constructor() {
    super();
    this.state = {
      showHideDetails: true,
      datum: "",
      budget: "",
      status: "",
      buildName: "",
      BuildNR: "",
      details: "",
      cardInfo: [],
      ownedBuilds: [],
      imageURL: ""
    };
    //this binden zodat een component weg gaat
    this.hideComponent = this.hideComponent.bind(this);
  }

  //Eerste dat uitgevoerd op de website
  //Kijken welke build de email van de gebruiker bevat
  //Alle gegevens uit de build halen en plaatsen in een state array
  componentDidMount() {
    var authDataEmail = firebase.auth().currentUser.email;
    //const user = useContext(AuthProvider);
    firebase.firestore().collection("Build")
      .where('UserEmail', 'array-contains', authDataEmail)
      .get()
      .then(querySnapshot => {
        
          querySnapshot.forEach((doc) => {
              // doc.data() is never undefined for query doc snapshots
              this.setState((prevState) => ({
                ownedBuilds: [...prevState.ownedBuilds, {
                  buildID: doc.data().BuildID,
                  bestelStatus: doc.data().BestelStatus,
                  budget: doc.data().Budget,
                  buildName: doc.data().Buildname,
                  datum: doc.data().Datum,
                  prijsMax: doc.data().PrijsMax,
                  prijsMin: doc.data().PrijsMin,
                  userMail: doc.data().UserEmail,
                  imageURL: doc.data().Image
                }],
              }));
          });          
        })
  }

  //Een component verwijderen (onzichtbaar maken)
  hideComponent(name) {
    switch (name) {
      case "showHideDetails":
        this.setState({ showHideDetails: !this.state.showHideDetails });
        break;
      default:
        null();
    }
  }
    //ID ophalen van de build waarop gedrukt werd.
    //Het pad sturen naar de hisotry en zo naar een andere pagina gaan
    changeRoute (index){
    localStorage.setItem("BuildIDNumber", this.state.ownedBuilds[index].buildID)
    var path = "./Build";
    this.props.history.push(path);
  };

  */
  render() {
    //Tellen van het aantal onderdelen in de array.
    var amountOfBuilds = 0;
    /*
    const cardInfo = [];

     //Pushen naar een lokale array die getoond kan worden op de pagina
    if (amountOfBuilds > 0) {
      for (var i = 0; i < amountOfBuilds; i++) {  
        cardInfo.push(this.state.ownedBuilds[i]);
      }
    } else {
      cardInfo.push({
        title: "Chosen title",
      });
    }

    */
    //Indien het aantal items gelijk is aan nul kan een andere pagina getoond worden.
    //Indien er geen build zijn wordt een pagina getoont met volgens mij een grappig woordje "Noting", de rest denkt dat het een typfout is... 
    //Het is dus geen typfout
    /*
    function checkAmount(card, index, showHideDetails) {
      if (amountOfBuilds === 0) {
        return cardInfo.map(renderMakeAmountCard);
      }
      return cardInfo.map(renderCard);
    }
    */

   

    //Functie om de kaart te renderen in geval dat er wel builds zijn
    const renderCard = (card, index, showHideDetails) => {
      const showHideDetails1 = this.state.showHideDetails;
      return (
        <Card style={{ width: "100%" }} key={index} className="mt-5">
          <Card.Header as="h5">
            {card.buildName}{" "}
            <button
              className="btn btn-link detailBuildButton visible"
              onClick={() => this.hideComponent("showHideDetails")}
            >
              Details
            </button>{" "}
          </Card.Header>

          <Card.Body>
            <Media>
              <Media.Body className="shortPCInfo">
                <Table>
                  <tbody>
                    <tr className="d-flex justify-content-center"> 
                      <img
                        height= "100%"
                        width= "100%"
                        className="showDetails buildListCenterBuildImage mb-3"
                        src={card.imageURL}
                        alt="Generic placeholder"
                      />
                    </tr>
                    {showHideDetails1 && (
                      <tr>
                        <td className="makeItDissapear">Datum</td>
                        <td className="makeItDissapear">{card.datum}</td>
                      </tr>
                    )}
                    {showHideDetails1 && (
                      <tr className="makeItDissapear">
                        {showHideDetails && (
                          <td className="makeItDissapear">Budget</td>
                        )}
                        {showHideDetails && (
                          <td className="makeItDissapear">{card.budget}</td>
                        )}
                      </tr>
                    )}
                    {showHideDetails1 && (
                      <tr className="makeItDissapear">
                        <td className="makeItDissapear">Bestel status</td>
                        <td className="makeItDissapear">{card.bestelStatus}</td>
                      </tr>
                    )}
                  </tbody>
                </Table>
                <Button
                  className="float-none smallSpecButton"
                  variant="primary"
                  
                  onClick={() => this.changeRoute(index)}
                >
                  Check the specs
                </Button>
              </Media.Body>
            </Media>
            <Card.Text>{card.text}</Card.Text>
          </Card.Body>
        </Card>
      );
    };

    //De knop maak build stuurt de gebruiker naar de pagina forms waar een build aangemaakt kan worden
    const routeChangeEmpty = () => {
      var path = "./Form";
      this.props.history.push(path);
    };

    
    //De main hub waar alle funcites opgeroepen worden.
    //Hier wordt enkel checkamount opgeroepen en daarna gebeurt de "magic"
    return (
      <React.Fragment>
        <NavigationBar />
        <div className="d-flex justify-content-center">
          <Card style={{ width: "75%" }} key={index} className="mt-5">
            <Card.Header>
              <div className="d-flex justify-content-center">
                <img
                  width={450}
                  height={350}
                  className=""
                  alt="Generic placeholder"
                />
              </div>
            </Card.Header>

            <Card.Body>
              <Media className="d-block align-content-center">
                <Media.Body className="shortPCInfo">
                  <div className="d-flex justify-content-center">
                    <Button
                      className="smallSpecButton"
                      variant="dark"
                      onClick={routeChangeEmpty}
                    >
                      Click here to make a build.
                    </Button>
                  </div>
                </Media.Body>
              </Media>
              <Card.Text>{card.text}</Card.Text>
            </Card.Body>
          </Card>
        </div>
        <FooterPage />
      </React.Fragment>
    );
  }
}

export default hamburgerMenuPage;
