import React, {useCallback, useContext} from "react";
import "bootstrap/dist/css/bootstrap.css";
import Form from "react-bootstrap/Form";
import Button from "react-bootstrap/Button";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import Image from "react-bootstrap/Image";
import { Container } from "react-bootstrap";
import {Redirect, withRouter} from "react-router";
import firebase from "./Firestore";
import { AuthContext } from "../Auth";

//login afhandelen
//In deze pagina wordt sterk gebruik gemaakt van het volgende video
//https://youtu.be/unr4s3jd9qA

//Ik zal mijn best doen om deze zo goed mogelijk uit te leggen

//Deze component krijgt history van de context
const Login = ({ history }) => {

  //Login  maken voor bestaande gebruikers
  //Functie om de login door te laten gaan
  const handleLogin = useCallback(
    async event => {
       //Pagina niet herladen
    event.preventDefault();
    //alle elementen uit de form (this) in een constante steken
    const {email, password} = event.target.elements;
    //Proberen om een user in te loggen met firebase api
    //Email en password doorsturen
    //Als iets fout gaat komt een alert
    try{
      await firebase
      .auth()
      .signInWithEmailAndPassword(email.value, password.value);
      history.push("/");
    } catch (error) {
      console.log(error);
    }
    //EmailID in de localstorage steken
    localStorage.setItem("EmailID", email.value);
  }, [history]);

  const {currentUser} = useContext(AuthContext);
 //Als alles goed gaat, wordt de user gepusht naar homepage
  if (currentUser) {
    return <Redirect to="/" />;
  }

  //Website lay out
  return (
    <div class="mean-fruit-gradient loginbackground">
      <div class="row ">
        <div class="col-sm-9 col-md-7 col-lg-5 mx-auto">
          <div class="card card-signin my-5">
            <div class="card-body">
              <Container>
              <Row className="justify-content-md-center">
                <Col ms={5} md={4}>
                  <Image  fluid className="border-bottom border-dark"/>
                </Col>
              </Row>
              <br></br>
              <h5 class="card-title text-center">Sign In</h5>
              </Container>
              <Form class="form-signin" onSubmit={handleLogin}>
                <Form.Group controlId="formBasicEmail">
                  <Form.Label>Email address</Form.Label>
                  <Form.Control type="email" name="email" placeholder="Email" />
                  <Form.Text className="text-muted">
                    We'll never share your email with anyone else.
                  </Form.Text>
                </Form.Group>
                <Form.Group controlId="formBasicPassword">
                  <Form.Label>Password</Form.Label>
                  <Form.Control type="password" name="password" placeholder="Password" />
                </Form.Group>
                <Form.Group controlId="formBasicCheckbox">
                  <Form.Check type="checkbox" label="Stay Signed In" />
                </Form.Group>
                <Form.Group>
                  <p>No account? make one <a href="./signup">here</a></p>
                  <p>Want to know more about this app? Learn more <a href="./about">here</a></p>
                  <p>Also you can read our privacy policy <a href="./Privacy">here</a></p>
                </Form.Group>
                <Button className="d-flex ml-auto mr-auto w-75 justify-content-md-center" variant="primary" type="submit">Login</Button>
              </Form>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default withRouter(Login);
